import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

  // Practice 3

  // Practice 2
  startNum = 10;
  newValue(val: number) {
    this.startNum += val
  }

  // Practice 1
  title = 'Day31 Angular Practice';
  disabled = true;
  textColour = "blue";
  toggleInput() {
    console.info("button clicked")
    this.disabled = !this.disabled;
  }
}
