import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'day31 Angular';
  disabled = true;
  textColour = "blue";

  startNum = 10;


  toggleInput() {
    console.info("button clicked")
    this.disabled = !this.disabled;
  }

  newValue(val: number) {
    this.startNum += val
  }


}
